  static List<Exercise> where((String, String)? criteria) {
    if (criteria == null) return items;

    switch(criteria.$1) {
      case 'id':
        return items.where((_) => _.id == criteria.$2).toList();
      case 'name':
        return items.where((_) => _.name == criteria.$2).toList();
    }
    return <Exercise>[];
  }
